mapper.update:
UPDATE BOARD_COMM
SET CBOARD_TITLE = #{cboard_title}, CBOARD_CONTENT = #{cboard_content}, COMM_UPDATE_DATE = sysdate
WHERE CBOARD_NO = #{cboard_no}

UPDATE BOARD_COMM
SET CBOARD_DELETE_CHK = 1, COMM_UPDATE_DATE = SYSDATE
WHERE CBOARD_NO = #{cboard_no}

UPDATE BOARD_COMM
SET CBOARD_VIEWCNT = CBOARD_VIEWCNT + 1
WHERE CBOARD_NO = #{cboard_no}
