mapper.select:
SELECT a.* FROM (SELECT ROWNUM rn , a.* FROM (SELECT B.CBOARD_NO, B.CBOARD_TITLE, B.CBOARD_VIEWCNT, B.CBOARD_DATE, B.COMM_BIG2 ,B.COMM_MID2, U.USER_ID, U.USER_NIC, (SELECT COUNT(creply_no) FROM BOARD_COMM_REPLY WHERE CREPLY_DELETE_CHK = 0 AND CBOARD_NO = B.CBOARD_NO) AS reply_count FROM BOARD_COMM B JOIN USERS U ON U.USER_NO = B.USER_NO WHERE B.COMM_BIG=200 AND B.COMM_MID = 20
AND B.COMM_MID2 = #{comm_mid2}
ORDER BY B.CBOARD_DATE DESC) a) a WHERE a.rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE BOARD_COMM
SET CBOARD_TITLE = #{cboard_title}, CBOARD_CONTENT = #{cboard_content}, COMM_UPDATE_DATE = sysdate
WHERE CBOARD_NO = #{cboard_no}

UPDATE BOARD_COMM
SET CBOARD_DELETE_CHK = 1, COMM_UPDATE_DATE = SYSDATE
WHERE CBOARD_NO = #{cboard_no}

UPDATE BOARD_COMM
SET CBOARD_VIEWCNT = CBOARD_VIEWCNT + 1
WHERE CBOARD_NO = #{cboard_no}

 		UPDATE BOARD_COMM
		SET COMM_MID2 = #{comm_mid2}
		WHERE CBOARD_NO = #{cboard_no}

mapper.select:
SELECT B.CBOARD_NO, B.CBOARD_TITLE, B.CBOARD_VIEWCNT, B.CBOARD_DATE, B.COMM_BIG2 ,B.COMM_MID2, U.USER_ID, U.USER_NIC, (SELECT COUNT(creply_no) FROM BOARD_COMM_REPLY WHERE CREPLY_DELETE_CHK = 0 AND CBOARD_NO = B.CBOARD_NO) AS reply_count FROM BOARD_COMM B, USERS U WHERE B.COMM_BIG=200 AND B.COMM_MID = 20 AND B.USER_NO = U.USER_NO
AND B.COMM_MID2 = #{comm_mid2}
AND (B.CBOARD_TITLE LIKE '%'||#{keyword}||'%' OR B.CBOARD_CONTENT LIKE '%'||#{keyword}||'%')
AND U.USER_NIC = #{keyword}
ORDER BY B.CBOARD_DATE DESC

SELECT BCF.CBOARD_NO, BCF.CBOARD_FILE_CNT, BCF.CBOARD_FILE_NAME, BCF.CBOARD_FILE_USER_NAME
FROM BOARD_COMM_FILE BCF 
WHERE bcf.CBOARD_NO = #{cboard_no}

mapper.update:
UPDATE BOARD_COMM_REPLY
SET CREPLY_DELETE_CHK = 1, COMM_UPDATE_DATE = sysdate
WHERE CREPLY_NO = #{creply_no}

UPDATE BOARD_COMM_REPLY
SET CREPLY_CONTENT = #{creply_content}, COMM_UPDATE_DATE = sysdate
WHERE creply_no = #{creply_no}
