mapper.select:

SELECT
*
FROM
(
    SELECT rownum rn, a.*
    FROM
    (select * from message WHERE msg_receiver = #{msgReceiver} order by msg_no DESC) a
)
WHERE rn >= #{start}
and rn <= #{end}


SELECT
*
FROM
(
    SELECT rownum rn, a.*
    FROM
    (select * from message WHERE msg_sender = #{msgSender} order by msg_no DESC) a
)
WHERE rn >= #{start}
and rn <= #{end}
